package llc.arma.checklisttag;import android.app.Application;import llc.arma.checklisttag.di.AppComponent;import llc.arma.checklisttag.di.DaggerAppComponent;import llc.arma.checklisttag.di.modules.ContextModule;import llc.arma.checklisttag.di.modules.DatabaseModule;public class App extends Application {    private static AppComponent appComponent;    @Override    public void onCreate() {        super.onCreate();        appComponent = DaggerAppComponent.builder()                .contextModule(new ContextModule(getApplicationContext()))                .databaseModule(new DatabaseModule(getApplicationContext()))                .build();    }    public static AppComponent getAppComponent() {        return appComponent;    }}