package llc.arma.checklisttag;import android.widget.TextView;import com.google.android.material.card.MaterialCardView;import androidx.databinding.BindingAdapter;import llc.arma.checklisttag.db.model.SmartObject;public class BindingAdapters {    @BindingAdapter("app:sync")    public static void syncStatus(TextView view, int sync){        switch (sync){            case 0: view.setText(view.getContext().getResources().getString(R.string.sync_status_synced)); break;            case 2: view.setText(view.getContext().getResources().getString(R.string.sync_status_waiting_for_sync)); break;        }    }    @BindingAdapter("app:type")    public static void type(TextView view, String type){        switch (type){            case SmartObject.BLE_MARK:            case SmartObject.NFC_MARK: view.setText(view.getContext().getResources().getString(R.string.tag_status_bind)); break;            case SmartObject.BLE_MARK_LINKED:            case SmartObject.NFC_MARK_LINKED: view.setText(view.getContext().getResources().getString(R.string.tag_status_linked)); break;            case SmartObject.BLE_MARK_NEW:            case SmartObject.NFC_MARK_NEW: view.setText(view.getContext().getResources().getString(R.string.tag_status_new)); break;        }    }    @BindingAdapter("app:typeColor")    public static void type(MaterialCardView view, String type){        switch (type){            case SmartObject.BLE_MARK:            case SmartObject.NFC_MARK: view.setCardBackgroundColor(view.getContext().getResources().getColor(R.color.colorInWork, view.getContext().getTheme())); break;            case SmartObject.BLE_MARK_LINKED:            case SmartObject.NFC_MARK_LINKED: view.setCardBackgroundColor(view.getContext().getResources().getColor(R.color.colorWaiting, view.getContext().getTheme())); break;            case SmartObject.BLE_MARK_NEW:            case SmartObject.NFC_MARK_NEW: view.setCardBackgroundColor(view.getContext().getResources().getColor(R.color.colorNotBind, view.getContext().getTheme())); break;        }    }}