package llc.arma.checklisttag;import android.os.Bundle;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;public class MainViewModel extends ViewModel {    private final MutableLiveData<Event> showMainEvent = new MutableLiveData<>();    public LiveData<Event> getShowMainEvent() {        return showMainEvent;    }    public MainViewModel(){        if(SharedHelper.isAuth())            showMainEvent.postValue(new Event(new Bundle()));    }}