package llc.arma.checklisttag.data;public class Ble {    private final String uuid;    private final String id;    private final int rssi;    public Ble(String uuid, String id, int rssi) {        this.uuid = uuid;        this.id = id;        this.rssi = rssi;    }    public String getUuid() {        return uuid;    }    public String getId() {        return id;    }    public int getRssi() {        return rssi;    }}