package llc.arma.checklisttag.data;import androidx.recyclerview.widget.DiffUtil;import java.util.List;public class BleDiffUtil extends DiffUtil.Callback {    private final List<Ble> oldList;    private final List<Ble> newList;    public BleDiffUtil(List<Ble> oldList, List<Ble> newList) {        this.oldList = oldList;        this.newList = newList;    }    @Override    public int getOldListSize() {        return oldList.size();    }    @Override    public int getNewListSize() {        return newList.size();    }    @Override    public boolean areItemsTheSame(int oldItemPosition, int newItemPosition) {        return oldList.get(oldItemPosition).getId().equals(newList.get(newItemPosition).getId());    }    @Override    public boolean areContentsTheSame(int oldItemPosition, int newItemPosition) {        return oldList.get(oldItemPosition).getRssi() == (newList.get(newItemPosition).getRssi())                && oldList.get(oldItemPosition).getId().equals(newList.get(newItemPosition).getId());    }}