package llc.arma.checklisttag.data;import llc.arma.checklisttag.db.model.SmartObject;public class Tag extends SmartObject {    public String getTagName(){        return this.getName();    }    public String getTagType(){        return getType();    }    public boolean isNfc(){        return getTagType().equals(SmartObject.NFC_MARK_NEW) ||                getTagType().equals(SmartObject.NFC_MARK) ||                getTagType().equals(SmartObject.NFC_MARK_LINKED);    }    public boolean isBle(){        return getTagType().equals(SmartObject.BLE_MARK_NEW) ||                getTagType().equals(SmartObject.BLE_MARK) ||                getTagType().equals(SmartObject.BLE_MARK_LINKED);    }    public void bindTag(String id){        if(getType().equals(SmartObject.NFC_MARK_NEW)) {            setType(SmartObject.NFC_MARK_LINKED);            setValue(SmartObject.TAG_ID, id);            setSync(2);            setLastupd(System.currentTimeMillis());        }        if(getType().equals(SmartObject.BLE_MARK_NEW)){            setType(SmartObject.BLE_MARK_LINKED);            setValue(SmartObject.TAG_ID, id);            setSync(2);            setLastupd(System.currentTimeMillis());        }    }}