package llc.arma.checklisttag.data;import llc.arma.checklisttag.db.model.SmartObject;public class Tag extends SmartObject {    public String getTagName(){        return this.getName();    }    public String getTagType(){        return getType();    }    public void bindTag(String id){        if(getType().equals(SmartObject.NFC_MARK_NEW)) {            setType(SmartObject.NFC_MARK_LINKED);            setValue(SmartObject.TAG_ID, id);            setSync(2);            setLastupd(System.currentTimeMillis());        }    }}