package llc.arma.checklisttag.data;import androidx.annotation.NonNull;import androidx.recyclerview.widget.DiffUtil;public class TagDiffUtil extends DiffUtil.ItemCallback<Tag> {    @Override    public boolean areItemsTheSame(@NonNull Tag oldItem, @NonNull Tag newItem) {        return oldItem.getSid().equals(newItem.getSid());    }    @Override    public boolean areContentsTheSame(@NonNull Tag oldItem, @NonNull Tag newItem) {        return oldItem.getLastupd() == newItem.getLastupd() && oldItem.getSync() == newItem.getSync();    }}