package llc.arma.checklisttag.data;import java.util.List;import java.util.stream.Collectors;import javax.inject.Inject;import llc.arma.checklisttag.net.WebUser;public class UserWebToSourceMapper extends Mapper<WebUser, UserEntity> {    @Inject    public UserWebToSourceMapper() {    }    @Override    public UserEntity map(WebUser input) {        WebUser.User user = input.getUser();        List<UserEntity.Group> groupList = user.getGroups().stream().map(group ->                new UserEntity.Group(group.getId(), group.getName())).collect(Collectors.toList());        return UserEntity.builder()                .id(user.getId())                .username(user.getUsername())                .firstName(user.getFirstName())                .lastName(user.getLastName())                .secondName(user.getSecondName())                .num(user.getNum())                .card(user.getCard())                .position(user.getPosition())                .phone(user.getPhone())                .email(user.getEmail())                .avatar(user.getAvatar())                .token(input.getToken())                .groups(groupList)                .organisation(UserEntity.Organisation.builder()                        .id(user.getOrganisation().getId())                        .name(user.getOrganisation().getName())                        .build())                .build();    }}