package llc.arma.checklisttag.db;import androidx.room.TypeConverter;import com.google.gson.Gson;import com.google.gson.reflect.TypeToken;import java.util.ArrayList;import java.util.List;public class Converters {    @TypeConverter    public String fromList(List<String> items){        if (items == null) items = new ArrayList<>();        return new Gson().toJson(items);    }    @TypeConverter    public List<String> fromString(String data){        return new Gson().fromJson(data, new TypeToken<List<String>>(){}.getType());    }}