package llc.arma.checklisttag.db.dao;import java.util.List;import androidx.lifecycle.LiveData;import androidx.paging.DataSource;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.OnConflictStrategy;import androidx.room.Query;import androidx.room.Transaction;import llc.arma.checklisttag.data.Tag;import llc.arma.checklisttag.db.entity.Object;@Daopublic interface ObjectDao {    @Transaction    @Query("SELECT * FROM object WHERE type IN (:types) ORDER BY name ASC")    DataSource.Factory<Integer, Tag> getAll(String... types);    @Transaction    @Query("SELECT * FROM object WHERE sync = 2")    List<Tag> getNotSynced();    @Transaction    @Query("SELECT * FROM object WHERE sync = 2")    LiveData<List<Tag>> getNotSyncedLiveData();    @Transaction    @Query("SELECT * FROM object WHERE sid =:sid")    Tag getBySid(String sid);    @Insert(onConflict = OnConflictStrategy.REPLACE)    void insert(Object object);    @Delete    void delete(Object object);    @Query("DELETE FROM object")    void deleteAll();}