package llc.arma.checklisttag.db.dao;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.OnConflictStrategy;import androidx.room.Query;import llc.arma.checklisttag.db.entity.Reg;import llc.arma.checklisttag.db.entity.Value;@Daopublic interface ValueDao {    @Query("SELECT * FROM value WHERE sid = :sid")    Value getBySid(String sid);    @Insert(onConflict = OnConflictStrategy.REPLACE)    void insert(Value value);    @Delete    void delete(Value value);}