package llc.arma.checklisttag.di.modules;import android.content.Context;import androidx.room.Room;import dagger.Module;import dagger.Provides;import llc.arma.checklisttag.R;import llc.arma.checklisttag.db.AppDatabase;import llc.arma.checklisttag.repo.LocalRepo;@Modulepublic class DatabaseModule {    private final AppDatabase appDatabase;    private final LocalRepo localRepo;    public DatabaseModule(Context context){        appDatabase = Room.databaseBuilder(context, AppDatabase.class,                context.getResources().getString(R.string.app_database_name)).build();        localRepo = new LocalRepo(appDatabase);    }    @Provides    public LocalRepo getLocalRepo(){        return localRepo;    }}