package llc.arma.checklisttag.net;public class AuthRequest {    private final String username;    private final String password;    private final AuthLoginPayload payload;    public AuthRequest(String username, String password, String deviceId) {        this.username = username;        this.password = password;        this.payload = new AuthLoginPayload(deviceId);    }    public AuthLoginPayload getAuthLoginPayload() {        return payload;    }    public String getUsername() {        return username;    }    public String getPassword() {        return password;    }    private static class AuthLoginPayload{        private final String deviceId;        @SuppressWarnings("FieldCanBeLocal")        private final String type = "MOB";        public String getDeviceId() {            return deviceId;        }        public String getType() {            return type;        }        public AuthLoginPayload(String deviceId) {            this.deviceId = deviceId;        }    }}