package llc.arma.checklisttag.ui.ble;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import java.util.ArrayList;import java.util.List;import androidx.annotation.NonNull;import androidx.databinding.DataBindingUtil;import androidx.recyclerview.widget.DiffUtil;import androidx.recyclerview.widget.RecyclerView;import llc.arma.checklisttag.R;import llc.arma.checklisttag.data.Ble;import llc.arma.checklisttag.data.BleDiffUtil;import llc.arma.checklisttag.databinding.BleItemBinding;public class BleAdapter extends RecyclerView.Adapter<BleAdapter.ViewHolder> {    private List<Ble> items = new ArrayList<>();    private final BleActionsListener actionsListener;    public BleAdapter(BleActionsListener actionsListener){        this.actionsListener = actionsListener;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        return new ViewHolder(DataBindingUtil.inflate(LayoutInflater.from(parent.getContext()),                R.layout.ble_item, parent, false), actionsListener);    }    @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        holder.bind(items.get(position));    }    @Override    public int getItemCount() {        return items.size();    }    public void setItems(List<Ble> items) {        BleDiffUtil bleDiffUtil = new BleDiffUtil(this.items, items);        this.items = items;        DiffUtil.DiffResult result = DiffUtil.calculateDiff(bleDiffUtil);        result.dispatchUpdatesTo(this);    }    public static class ViewHolder extends RecyclerView.ViewHolder {        private final BleItemBinding bleItemBinding;        public ViewHolder(@NonNull BleItemBinding bleItemBinding,                          BleActionsListener actionsListener) {            super(bleItemBinding.getRoot());            this.bleItemBinding = bleItemBinding;            this.bleItemBinding.setActionsListener(actionsListener);        }        public void bind(Ble ble){            bleItemBinding.setBle(ble);        }    }}