package llc.arma.checklisttag.ui.login;import android.content.Context;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.databinding.DataBindingUtil;import androidx.fragment.app.Fragment;import androidx.lifecycle.ViewModelProvider;import androidx.navigation.Navigation;import llc.arma.checklisttag.R;import llc.arma.checklisttag.databinding.LoginFragmentBinding;public class LoginFragment extends Fragment {    private LoginFragmentBinding binding;    private LoginViewModel viewModel;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        binding = DataBindingUtil.inflate(inflater, R.layout.login_fragment, container,                false);        binding.setLifecycleOwner(getViewLifecycleOwner());        binding.setModel(viewModel);        return binding.getRoot();    }    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        viewModel.getShowMainEvent().observe(getViewLifecycleOwner(), event -> {            if(event.isHandled()){                Navigation.findNavController(requireActivity(), R.id.mainContainer)                        .navigate(R.id.action_loginFragment_to_tagsFragment);            }        });    }    @Override    public void onAttach(@NonNull Context context) {        super.onAttach(context);        viewModel = new ViewModelProvider(requireActivity()).get(LoginViewModel.class);    }}