package llc.arma.checklisttag.ui.login;import android.os.Bundle;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;import llc.arma.checklisttag.Event;import llc.arma.checklisttag.SharedHelper;import llc.arma.checklisttag.Utils;import llc.arma.checklisttag.repo.RemoteRepo;public class LoginViewModel extends ViewModel {    public final MutableLiveData<String> login = new MutableLiveData<>();    public final MutableLiveData<String> pass = new MutableLiveData<>();    private final MutableLiveData<Event> showMainEvent = new MutableLiveData<>();    private final MutableLiveData<Boolean> isLoadingState = new MutableLiveData<>();    public LiveData<Event> getShowMainEvent() {        return showMainEvent;    }    public LiveData<Boolean> getIsLoadingState() {        return isLoadingState;    }    public void onLoginClicked(){        if(login.getValue() != null && pass.getValue() != null) {            isLoadingState.postValue(true);            RemoteRepo.login(login.getValue(), Utils.MD5(pass.getValue()))                    .observeForever(pair -> {                        SharedHelper.setToken(pair.second);                        isLoadingState.postValue(false);                        showMainEvent.postValue(new Event(new Bundle()));                    });        }    }}