package llc.arma.checklisttag.ui.scan;import android.os.Bundle;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;import java.util.concurrent.Executors;import llc.arma.checklisttag.App;import llc.arma.checklisttag.Event;public class ScanViewModel extends ViewModel {    private String sid;    private final MutableLiveData<Event> closeEvent = new MutableLiveData<>();    private final MutableLiveData<Event> setResultEvent = new MutableLiveData<>();    private final MutableLiveData<Event> showNfcExistWarningEvent = new MutableLiveData<>();    public LiveData<Event> getShowNfcExistWarningEvent() { return showNfcExistWarningEvent; }    public LiveData<Event> getSetResultEvent() {        return setResultEvent;    }    public LiveData<Event> getCloseEvent() {        return closeEvent;    }    public void start(String sid){        this.sid = sid;    }    public void onScanned(String nfcId){        Executors.newSingleThreadExecutor().execute(() -> {            if(!App.getAppComponent().provideLocalRepo().isNfcTagExist(nfcId)) {                Bundle bundle = new Bundle();                bundle.putString("sid", sid);                bundle.putString("nfcId", nfcId);                setResultEvent.postValue(new Event(bundle));            }else{                showNfcExistWarningEvent.postValue(new Event(new Bundle()));            }        });    }    public void onCloseClicked(){        closeEvent.postValue(new Event(new Bundle()));    }}