package llc.arma.checklisttag.ui.tag;import android.view.LayoutInflater;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.databinding.DataBindingUtil;import androidx.paging.PagedListAdapter;import androidx.recyclerview.widget.DiffUtil;import androidx.recyclerview.widget.RecyclerView;import llc.arma.checklisttag.R;import llc.arma.checklisttag.data.Tag;import llc.arma.checklisttag.databinding.TagItemBinding;public class TagAdapter extends PagedListAdapter<Tag, TagAdapter.TagViewHolder> {    private final TagActionsListener actionsListener;    public TagAdapter(@NonNull DiffUtil.ItemCallback<Tag> diffCallback,                      TagActionsListener actionsListener) {        super(diffCallback);        this.actionsListener = actionsListener;    }    @NonNull    @Override    public TagViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        return new TagViewHolder(DataBindingUtil.inflate(LayoutInflater.from(parent.getContext()),                R.layout.tag_item, parent, false));    }    @Override    public void onBindViewHolder(@NonNull TagViewHolder holder, int position) {        holder.bind(getItem(position), actionsListener);    }    public static class TagViewHolder extends RecyclerView.ViewHolder{        private final TagItemBinding binding;        public TagViewHolder(@NonNull TagItemBinding binding) {            super(binding.getRoot());            this.binding = binding;        }        public void bind(Tag tag, TagActionsListener actionsListener){            binding.setTag(tag);            binding.setActionsListener(actionsListener);        }    }}