package llc.arma.checklisttag.ui.tag;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.lifecycle.Lifecycle;import androidx.viewpager2.adapter.FragmentStateAdapter;import org.jetbrains.annotations.NotNull;import llc.arma.checklisttag.ui.tag.ble.BleTagFragment;import llc.arma.checklisttag.ui.tag.nfc.NfcTagFragment;public class TagPager extends FragmentStateAdapter {    public TagPager(@NotNull FragmentManager fragmentManager, @NotNull Lifecycle lifecycle) {        super(fragmentManager, lifecycle);    }    @NonNull    @NotNull    @Override    public Fragment createFragment(int position) {        if(position == 0){            return new NfcTagFragment();        }else {            return new BleTagFragment();        }    }    @Override    public int getItemCount() {        return 2;    }}