package llc.arma.checklisttag.ui.tag;import android.content.Context;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Toast;import com.google.gson.JsonParseException;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.databinding.DataBindingUtil;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentResultListener;import androidx.lifecycle.Observer;import androidx.lifecycle.ViewModelProvider;import androidx.navigation.Navigation;import androidx.swiperefreshlayout.widget.SwipeRefreshLayout;import llc.arma.checklisttag.R;import llc.arma.checklisttag.data.Tag;import llc.arma.checklisttag.databinding.TagsFragmentBinding;import llc.arma.checklisttag.data.TagDiffUtil;import llc.arma.checklisttag.repo.RemoteRepo;public class TagsFragment extends Fragment implements TagActionsListener, FragmentResultListener,        SwipeRefreshLayout.OnRefreshListener {    private TagsFragmentBinding binding;    private TagsViewModel viewModel;    private TagAdapter adapter;    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        adapter = new TagAdapter(new TagDiffUtil(), this);    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        binding = DataBindingUtil.inflate(inflater, R.layout.tags_fragment, container,                false);        binding.setLifecycleOwner(getViewLifecycleOwner());        binding.setModel(viewModel);        return binding.getRoot();    }    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        binding.getRoot().setOnApplyWindowInsetsListener((v, insets) -> {            binding.getRoot().setPadding(insets.getSystemWindowInsetLeft(),                    insets.getSystemWindowInsetTop(),                    insets.getSystemWindowInsetRight(),                    insets.getSystemWindowInsetBottom());            return insets;        });        binding.tagRecycler.setAdapter(adapter);        binding.pullToRefresh.setOnRefreshListener(this);        viewModel.getPagedList().observe(getViewLifecycleOwner(), adapter::submitList);        viewModel.getShowScanEvent().observe(getViewLifecycleOwner(), event -> {            if(event.isHandled()){                Navigation.findNavController(requireActivity(), R.id.mainContainer)                        .navigate(R.id.action_tagsFragment_to_scanFragment, event.getContent());            }        });        viewModel.getShowLoginEvent().observe(getViewLifecycleOwner(), event -> {            if(event.isHandled()){                Navigation.findNavController(requireActivity(), R.id.mainContainer)                        .navigate(R.id.action_tagsFragment_to_loginFragment2);            }        });        viewModel.getShowWriteEvent().observe(getViewLifecycleOwner(), event -> {            if(event.isHandled()){                Navigation.findNavController(requireActivity(), R.id.mainContainer)                        .navigate(R.id.action_tagsFragment_to_writeFragment);            }        });        viewModel.getLoadResult().observe(getViewLifecycleOwner(), aBoolean -> {            if(aBoolean){                Toast.makeText(requireContext(), "Success", Toast.LENGTH_SHORT).show();            }        });        requireActivity().getSupportFragmentManager()                .setFragmentResultListener("nfcScan", getViewLifecycleOwner(), this);        viewModel.loadTags();    }    @Override    public void onAttach(@NonNull Context context) {        super.onAttach(context);        viewModel = new ViewModelProvider(requireActivity()).get(TagsViewModel.class);    }    @Override    public void onTagClicked(Tag tag) {        viewModel.onTagClicked(tag);    }    @Override    public void onFragmentResult(@NonNull String requestKey, @NonNull Bundle result) {        viewModel.onTagScanned(result.getString("sid"), result.getString("nfcId"));    }    @Override    public void onRefresh() {        viewModel.loadTags();        binding.pullToRefresh.setRefreshing(false);    }}