package llc.arma.checklisttag.ui.tag;import android.os.Bundle;import org.jetbrains.annotations.NotNull;import java.util.concurrent.Executors;import androidx.lifecycle.LiveData;import androidx.lifecycle.MediatorLiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;import androidx.paging.LivePagedListBuilder;import androidx.paging.PagedList;import llc.arma.checklisttag.App;import llc.arma.checklisttag.Event;import llc.arma.checklisttag.SharedHelper;import llc.arma.checklisttag.data.Tag;import llc.arma.checklisttag.repo.RemoteRepo;public class TagsViewModel extends ViewModel {    private final MediatorLiveData<Boolean> loadResult = new MediatorLiveData<>();    private final MutableLiveData<Event> showLoginEvent = new MutableLiveData<>();    private final MutableLiveData<Event> showWriteEvent = new MutableLiveData<>();    public LiveData<Event> getShowWriteEvent() {        return showWriteEvent;    }    public LiveData<Event> getShowLoginEvent() {        return showLoginEvent;    }    public LiveData<Boolean> getLoadResult() {        return loadResult;    }    public void onLogoutClicked(){        showLoginEvent.postValue(new Event(new Bundle()));        SharedHelper.setToken(null);    }    public void onTagScanned(String sid, String nfcId){        App.getAppComponent().provideLocalRepo().bindTag(sid, nfcId);    }    public void onWriteClicked(){        showWriteEvent.postValue(new Event(new Bundle()));    }}